mapper.select:
SELECT 
  * 
FROM spmember
WHERE

	id = #{id}


	nick_name = #{nick_name}

mapper.update:
UPDATE euser_detail
SET
  phone=#{phone},
  email=#{email},
  extension_num=#{extension_num}
WHERE user_num=#{user_num}

mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT * FROM spmember m LEFT OUTER JOIN spmember_detail d ON m.mem_num = d.mem_num
ORDER BY d.reg_date DESC NULLS LAST)a)
WHERE rnum >= #{start} AND rnum <= #{end}
